import React, { useState } from "react";
import { Box, Typography, TextField, Button } from "@mui/material";

const Auth = () => {
  const [isSignup, setIsSignup] = useState(false);
  const [inputs, setInputs] = useState({
    name: "",
    email: "",
    password: "",
  });
  return (
    <div>
      <form>
        <Box
          display="flex"
          flexDirection={"column"}
          maxWidth={400}
          alignItems="center"
          justifyContent={"center"}
          margin="auto"
          marginTop={5}
          padding={3}
          borderRadius={5}
          boxShadow={"5px 5px 10px #ccc"}
          sx={{
            ":hover": {
              boxShadow: "10px 10px 20px #ccc",
            },
          }}
        >

          <Typography
            variant="h2"
            padding={3}
            textAlign="center"
            color={"black"}
          >
            {isSignup ? "Cadastro" : "Login"}
          </Typography>

          {isSignup && (
            <TextField
              name="name"
              margin="normal"
              type={"text"}
              variant="outlined"
              placeholder="Name"
            />
          )}

          <TextField
            name="email"
            margin="normal"
            type={"email"}
            variant="outlined"
            placeholder="Email"
          />

          <TextField
            name="password"
            margin="normal"
            type={"password"}
            variant="outlined"
            placeholder="Password"
          />

          <Button
            sx={{ marginTop: 3, borderRadius: 3 }}
            variant="contained"
            color="warning"
          >
            {isSignup ? "Cadastre-se" : "Login"}
          </Button>

          <Button
            sx={{ marginTop: 3, borderRadius: 3 }}
            onClick={() => setIsSignup(!isSignup)}
          >
            Vá para o {isSignup ? "Login" : "Cadastro"}
          </Button>
        </Box>
      </form>
    </div>
  );
};

export default Auth;
